mapper.select:
select *
from admin
where userid=#{userID} and topicid=#{topicID}

mapper.update:
update admin_apply
set is_finish=1
where applyid=#{applyID}

update topic
set is_delete=1
where topicid=#{topicID}

update post
set is_delete=1
where postid=#{postID}

update comment
set is_delete=1
where commentid=#{commentID}

update review
set is_delete=1
where reviewid=#{reviewID}
